import React, { useState } from 'react';
import { Button, TextField, List, ListItem, Typography, Snackbar, Alert } from '@mui/material';
import './addeducatortoatournament.css';

function AddEducatorToaTournament() {
    const [userIds, setUserIds] = useState([]);
    const [currentId, setCurrentId] = useState('');
    const [openSnackbar, setOpenSnackbar] = useState(false);

    const handleIdChange = (event) => {
        setCurrentId(event.target.value);
    };

    const handleAddId = () => {
        if (currentId && !userIds.includes(currentId)) {
            setUserIds([...userIds, currentId]);
            setCurrentId('');
            setOpenSnackbar(true);
        }
    };

    const handleSubmit = () => {
        console.log('ID inviati:', userIds);
        // Aggiungi qui la tua logica per l'invio degli ID
    };

    const handleCloseSnackbar = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenSnackbar(false);
    };

    return (
        <div className="App">
            <Typography variant="h4" gutterBottom>Aggiungi ID Utente al Torneo</Typography>
            <TextField
                label="Inserisci ID Utente"
                variant="outlined"
                value={currentId}
                onChange={handleIdChange}
                style={{ marginBottom: '20px', width: '300px' }}
            />
            <Button variant="contained" color="primary" onClick={handleAddId} style={{ marginBottom: '20px' }}>
                Aggiungi ID
            </Button>
            <List>
                {userIds.map(id => (
                    <ListItem key={id}>{id}</ListItem>
                ))}
            </List>
            <Button variant="contained" color="secondary" onClick={handleSubmit}>
                Invia ID
            </Button>
            <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
                    ID aggiunto
                </Alert>
            </Snackbar>
        </div>
    );
}

export default AddEducatorToaTournament;
