import React, { useState, useEffect } from 'react';
import './joinbattle.css';

const JoinBattle = () => {
    const [battles, setBattles] = useState([]);
    const [selectedBattle, setSelectedBattle] = useState('');
    const [studentId, setStudentId] = useState('');

    useEffect(() => {
        // Fetch the list of battles the student is subscribed to
        // This is a placeholder, replace with actual data fetching logic
        setBattles(['Battle 1', 'Battle 2', 'Battle 3']);
    }, []);

    const handleBattleChange = (event) => {
        setSelectedBattle(event.target.value);
    };

    const handleStudentIdChange = (event) => {
        setStudentId(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Add logic to join the battle
        console.log(`Student ID ${studentId} is joining ${selectedBattle}`);
    };

    return (
        <div className="joinBattle">
            <form onSubmit={handleSubmit}>
                <label>
                    Select Battle:
                    <select value={selectedBattle} onChange={handleBattleChange}>
                        <option value="">Select a Battle</option>
                        {battles.map((battle, index) => (
                            <option key={index} value={battle}>{battle}</option>
                        ))}
                    </select>
                </label>
                <label>
                    Student ID:
                    <input type="text" value={studentId} onChange={handleStudentIdChange} />
                </label>
                <button type="submit">Join Battle</button>
            </form>
        </div>
    );
};

export default JoinBattle;
