import React, { useState } from 'react';
import './createtournament.css'; // Importing the CSS file

const CreateTournament = () => {
    const [Name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [submissionDeadline, setSubmissionDeadline] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const userId = "User's ID"; // Fetch or set the user ID here
        const tournamentData = { tournamentName, description, submissionDeadline, userId };

        // Send tournamentData to the backend
        // Example: await fetch('your-backend-endpoint', { method: 'POST', body: JSON.stringify(tournamentData), headers: { 'Content-Type': 'application/json' } });
    };

    return (
        <div className="create-tournament">
            <form onSubmit={handleSubmit}>
                <label>
                    Tournament Name:
                    <input
                        type="text"
                        value={tournamentName}
                        onChange={(e) => setTournamentName(e.target.value)}
                    />
                </label>
                <label>
                    Description:
                    <textarea
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </label>
                <label>
                    Submission Deadline:
                    <input
                        type="date"
                        value={submissionDeadline}
                        onChange={(e) => setSubmissionDeadline(e.target.value)}
                    />
                </label>
                <button type="submit">Create Tournament</button>
            </form>
        </div>
    );
};

export default CreateTournament;
