import React, { useState } from 'react';
import styled from 'styled-components';
import { Button, Container, Row, Col } from 'react-bootstrap';

const ProfileContainer = styled(Container)`
  height: 100vh;
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProfileCard = styled.div`
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 30px;
  text-align: center;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
`;

const ProfileImage = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid white;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
`;

const ChangePhotoButton = styled(Button)`
  margin-top: 20px;
  background-color: #6a82fb;
  border: none;
  &:hover {
    background-color: #556cd6;
  }
`;

const Nickname = styled.h2`
  margin-top: 20px;
`;

const Biography = styled.p`
  margin-top: 20px;
`;

const CustomButton = styled(Button)`
  margin-top: 20px;
  background-color: #ff758c;
  border: none;
  &:hover {
    background-color: #ff586b;
  }
`;

const StudentProfile = () => {
    const [profilePic, setProfilePic] = useState('/path/to/default/profile.jpg');
    const [nickname, setNickname] = useState('NicknameStudente');

    const handleProfilePicChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setProfilePic(URL.createObjectURL(event.target.files[0]));
        }
    };

    return (
        <ProfileContainer>
            <ProfileCard>
                <ProfileImage src={profilePic} alt="Profile" />
                <input type="file" onChange={handleProfilePicChange} id="file-input" hidden />
                <label htmlFor="file-input">
                    <ChangePhotoButton as="span">Change Photo</ChangePhotoButton>
                </label>
                <Nickname>{nickname}</Nickname>
                <Biography>Welcome to your interactive profile! Share something interesting about yourself.</Biography>
                <Row>
                    <Col>
                        <CustomButton onClick={() => window.location.href = '/view-all-tournaments'}>View All Tournaments</CustomButton>
                    </Col>
                    <Col>
                        <CustomButton onClick={() => window.location.href = `/mysection/${nickname}`}>My Section</CustomButton>
                    </Col>
                </Row>
            </ProfileCard>
        </ProfileContainer>
    );
};

export default StudentProfile;
