import React, { useState } from 'react';
import { Button, TextField, List, ListItem, Typography, Snackbar } from '@mui/material';
import './addeducatortoatournament.css';

function AddeducatorToaTournament() {
    const [userIds, setUserIds] = useState([]);
    const [currentId, setCurrentId] = useState('');
    const [openSnackbar, setOpenSnackbar] = useState(false);

    const handleIdChange = (e) => {
        setCurrentId(e.target.value);
    };

    const handleAddId = () => {
        if (currentId && !userIds.includes(currentId)) {
            setUserIds(prevIds => [...prevIds, currentId]);
            setCurrentId('');
            setOpenSnackbar(true);
        }
    };

    const handleSubmit = () => {
        console.log('ID inviati:', userIds);
        // Aggiungi qui la logica per l'invio degli ID
    };

    return (
        <div className="App">
            <Typography variant="h4" gutterBottom>Aggiungi ID Utente al Torneo</Typography>
            <TextField
                label="Inserisci ID Utente"
                variant="outlined"
                value={currentId}
                onChange={handleIdChange}
                style={{ marginBottom: '20px' }}
            />
            <Button variant="contained" color="primary" onClick={handleAddId}>Aggiungi ID</Button>
            <List>
                {userIds.map(id => (
                    <ListItem key={id}>{id}</ListItem>
                ))}
            </List>
            <Button variant="contained" color="secondary" onClick={handleSubmit}>Invia ID</Button>
            <Snackbar
                open={openSnackbar}
                autoHideDuration={6000}
                onClose={() => setOpenSnackbar(false)}
                message="ID aggiunto"
            />
        </div>
    );
}

export default AddeducatorToaTournament;
