import React, {useEffect, useState} from 'react';
import './StudentProfile.css';
import sfondo from './sfondo.jpg';
import axios from "axios";
const StudentProfile = () => {
    const [profilePic, setProfilePic] = useState(sfondo);
    const [nickname, setNickname] = useState('NicknameStudente');

    const handleProfilePicChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setProfilePic(URL.createObjectURL(event.target.files[0]));
        }
    };

    useEffect(() => {
        axios.get('http://192.168.232.18:8080/getId/')
            .then(response => {
                setTournaments(response.data);
            })
            .catch(error => {goToerrorpage()} );
    }, []);

    return (
        <div className="profile-container">
            <div className="profile-card">
                <div className="top-section">
                    <img src={profilePic} alt="Profile" className="profile-pic"/>
                    <input type="file" onChange={handleProfilePicChange} id="file-input" hidden/>
                    <label htmlFor="file-input" className="image-change-button">Change Photo</label>
                </div>
                <div className="bottom-section">
                    <h2 className="nickname">{nickname}</h2>
                    <p className="welcome-message">Welcome to your student profile!</p>
                    <div className="action-buttons">
                        <button onClick={() => window.location.href = '/tournaments'}>View All Tournaments</button>
                        <button onClick={() => window.location.href = `/mysection/${nickname}`}>My Section</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StudentProfile;
