import React, { useState } from 'react';
import './StudentProfile.css';

const StudentProfile = () => {
    const [profilePic, setProfilePic] = useState('/path/to/default/profile.jpg'); // Percorso dell'immagine profilo di default
    const [nickname, setNickname] = useState('NicknameStudente');

    const handleProfilePicChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setProfilePic(URL.createObjectURL(event.target.files[0]));
        }
    };

    return (
        <div className="student-profile">
            <div className="profile-header">
                <img src={profilePic} alt="Profile" className="profile-pic" />
                <input type="file" onChange={handleProfilePicChange} className="profile-pic-upload" />
                <h2 className="nickname">{nickname}</h2>
            </div>
            <div className="profile-actions">
                <button onClick={() => window.location.href = '/view-all-tournaments'}>View All Tournaments</button>
                <button onClick={() => window.location.href = `/mysection/${nickname}`}>My Section</button>
            </div>
            <div className="additional-info">
                <p>Welcome to your profile! Here you can manage your tournaments, view your progress, and more.</p>
            </div>
        </div>
    );
};

export default StudentProfile;
