import React from 'react';
import { useNavigate } from 'react-router-dom';
import './BattleCardStudent.css';
import joinbattle from "../JoinBattle/joinbattle";
import quitbattle from "../QuitBattle/quitbattle";

function BattleCardStudent({ battle}) {
    const navigate = useNavigate();

    const joinBattle = (e) => {
        e.stopPropagation(); // Impedisce al click sul bottone di attivare il click sulla card
        navigate(`/joinbattle`);
    };

    const quitBattle = (e) => {
        e.stopPropagation(); // Impedisce al click sul bottone di attivare il click sulla card
        navigate(`/quitbattle`);
    };

    return (
        <div className="battle-card">
            <h3>{battle.name}</h3>
            <p>Dettagli: {battle.details}</p>
            <button className="join-button" onClick={joinBattle}>Join the Tournament</button>
            <button className="quit-button" onClick={quitBattle}>Quit the Tournament</button>
            {/* Altri dettagli della battaglia possono essere aggiunti qui */}
        </div>
    );
}

export default BattleCardStudent;
